'use strict';

import { createSelector } from 'reselect'
import indexBy from 'lodash.indexby'
import pluck from 'lodash.pluck'

export const agentTypes = state => state.agentTypes
export const agentTypesById = createSelector(
  [agentTypes],
  (agentTypes) => indexBy(agentTypes, 'id')
)
export const agentIds = createSelector(
  [agentTypes],
  (agentTypes) => pluck(agentTypes, 'id')
)
