import { createSelector } from 'reselect'
import indexBy from 'lodash.indexby'
import pluck from 'lodash.pluck'

export const agents = state => state.agents

export const getAgentGraphId = state => state.agentGraphId

export const agentsById = createSelector(
  [agents],
  (agents) => indexBy(agents, 'id')
)
export const agentIds = createSelector(
  [agents],
  (agents) => pluck(agents, 'id')
)
