'use strict';

const agents = [
  'agents'
]

const relationshipsByAgent = [
  ['agents'],
  ['relationships'],
  function (agents, relationships) {
    return agents.map(function (agent, agentId) {
      return relationships.filter(function (rel, relId) {
        return (
          rel.source === agentId ||
          rel.target === agentId
        )
      })
    })
  }
]

module.exports = {
  agents,
  relationshipsByAgent
}
