var UrlRe = require('url-regexp');
var config = require('uiconfig');
var Base = require('ampersand-model');
var debug = require('debug')("craftodex:ui:base:model");

var registry = require('registry');

module.exports = Base.extend({

  typeAttribute: "modelType",
  idAttribute: "@id",
  namespaceAttribute: "@graph",

  extraProperties: 'ignore',

  initialize: function () {
    debug("adding", this.getId(), "to registry");
    registry.set(this.getId(), this);
    this.on('destroy', function() {
      debug("deleting", this.getId(), "from registry");
      registry['delete'](this.getId());
    }, this);
  },

  props: {
    "@id": "string",
    "@type": "string",
    "@context": "object",
  },

  derived: {
    id: {
      deps: ["@id"],
      fn: function () {
        var id = this.getId();
        var api = config.api;

        if (!id) {
          return null;
        }

        var subId = id.substring(0, api.length);
        if (id.substring(0, api.length) === api) {
          return id.substring(api.length);
        } else {
          return id;
        }
      },
    },
  },

  url: function () {
    var id = this.getId();
    var api = config.api;

    var url = null;
    if (UrlRe.validate(id)) {
      url = id;
    } else if (id) {
      url = api + "/" + id
      ;
    }

    debug("url() ->", url, this);
    return url;
  },
});
