var debug = require('debug')("collections")
var _ = require('lodash')
var camelCase = require('camel-case')

// compute collections from types
var types = require('types')

var collections = {}
_.forEach(types, function (type, name) {
  var pluralName = camelCase(type.Collection.prototype.collectionType)
  collections[pluralName] = type
});

debug(collections);

module.exports = collections;
