var React = require('react')
var r = require('r-dom')

module.exports = React.createClass({

  propTypes: {
    style: React.PropTypes.object,
    tags: React.PropTypes.array
  },

  render: function () {
    return r.div({
      className: 'header-container',
      style: this.props.style
    }, this.props.tags)
  }
})
