var React = require('react')
var r = require('r-dom')

var debug = require('debug')

var Filter = React.createClass({

  getInitialState: function () {
    return {
      value: 5,
      min: 0,
      max: 10,
    }
  },

  render: function () {
    return (
      r.input({
        type: "range",
        value: this.state.value,
        min: this.state.min,
        max: this.state.max,
      })
    )
  },
})
