var React = require('react')

var debug = require('debug')
var h = require('create-element')

var Filter = React.createClass({

	getInitialState: function () {
		return {
			value: 5,
			min: 0,
			max: 10
		}
	},
	render: function () {
		return (
			<input 
				type="range"
				value={this.state.value}
				min={this.state.min}
				max={this.state.max} />
		)
	}
})


