var Url = require('url')
var config = require('config')
var prefix = Url.format(config.api)
var member = prefix + '/roleTypes/member'
var group = prefix + '/roleTypes/group'
var steward = prefix + '/roleTypes/steward'
var stewardee = prefix + '/roleTypes/stewardee'
var mentor = prefix + '/roleTypes/mentor'
var mentee = prefix + '/roleTypes/mentee'
var subgroup = prefix + '/roleTypes/subgroup'
var parentGroup = prefix + '/roleTypes/parent-group'

var memberLink = member + '/link'
var groupLink = group + '/link'
var stewardLink = steward + '/link'
var stewardeeLink = stewardee + '/link'
var mentorLink = mentor + '/link'
var menteeLink = mentee + '/link'

var subgroupLink = subgroup + '/link'
var parentGroupLink = parentGroup + '/link'

// targetAgent Roles
//   role: member
//     agentType: Person -> "[People who belong to] [targetAgent]"
//     agentType: Group -> "[Groups] that [belong to] [targetAgent]"
//   role: steward
//     agentType: Person -> "[People] who [steward] [targetAgent]"
//
// Inset:
//   contextAgent Roles and Links
//     role: steward -> "a steward in [contextAgent]"
//     link: stewardeeLink -> "is stewarded by"
//   targetAgent Links
//     link: memberLink -> "belongs to [targetAgent]"

var Labels = {
  targetAgent: {},
  contextAgent: {}
}

// Headers:
Labels.targetAgent[member] = { template: '{} belong to {} ', index: 0 }
Labels.targetAgent[group] = { template: '{} include {}', index: 0 }
Labels.targetAgent[subgroup] = { template: '{} are part of {}', index: 0 }
Labels.targetAgent[parentGroup] = { template: '{} encompass {}', index: 0 }
Labels.targetAgent[steward] = { template: '{} steward {}', index: 0 }
Labels.targetAgent[stewardee] = { template: '{} are stewarded by {}', index: 0 }
Labels.targetAgent[mentor] = { template: '{} mentor {}', index: 0 }
Labels.targetAgent[mentee] = { template: '{} are mentored by {}', index: 0 }

Labels.Person = "People who"
Labels.Group = "Groups that"

// Inset:
Labels.targetAgent[memberLink] = { template: 'belongs to {}' }
Labels.targetAgent[parentGroupLink] = { template: 'encompasses {}' }
Labels.targetAgent[subgroupLink] = { template: 'are part of {}' }
Labels.targetAgent[groupLink] = { template: 'include {}' }
Labels.targetAgent[mentorLink] = { template: 'mentor {}' }
Labels.targetAgent[menteeLink] = { template: 'are mentored by {}' }


Labels.targetAgent[stewardLink] = { template: 'stewards {}' }
Labels.targetAgent[stewardeeLink] = { template: 'are stewarded by {}' }

Labels.contextAgent[steward] = { template: 'a steward in {}' }
Labels.contextAgent[stewardee] = { template: 'a stewardee in {}' }

Labels.contextAgent[stewardeeLink] = { label: 'is stewarded by' }
Labels.contextAgent[stewardLink] = { label: 'is steward of' }




module.exports = Labels
