var Url = require('url')
var config = require('config')
var prefix = Url.format(config.api)
var member = prefix + '/roleTypes/member'
var group = prefix + '/roleTypes/group'
var steward = prefix + '/roleTypes/steward'
var stewardee = prefix + '/roleTypes/stewardee'
var mentor = prefix + '/roleTypes/mentor'
var mentee = prefix + '/roleTypes/mentee'
var subgroup = prefix + '/roleTypes/subgroup'
var parentGroup = prefix + '/roleTypes/parent-group'

var vars = require('ui/vars')

var memberLink = member + '/link'
var groupLink = group + '/link'
var stewardLink = steward + '/link'
var stewardeeLink = stewardee + '/link'
var mentorLink = mentor + '/link'
var menteeLink = mentee + '/link'

var subgroupLink = subgroup + '/link'
var parentGroupLink = parentGroup + '/link'

// targetAgent Roles
//   role: member
//     agentType: Person -> "[People who belong to] [targetAgent]"
//     agentType: Group -> "[Groups] that [belong to] [targetAgent]"
//   role: steward
//     agentType: Person -> "[People] who [steward] [targetAgent]"
//
// Inset:
//   contextAgent Roles and Links
//     role: steward -> "a steward in [contextAgent]"
//     link: stewardeeLink -> "is stewarded by"
//   targetAgent Links
//     link: memberLink -> "belongs to [targetAgent]"

var Labels = {
  targetAgent: {},
  contextAgent: {}
}

// Headers:
// Labels.targetAgent[member] = { template: '{} belong to {} ', index: 0, label: 'People' }
// Labels.targetAgent[group] = { template: '{} include {}', index: 0, label: 'Groups' }
// Labels.targetAgent[subgroup] = { template: '{} are part of {}', index: 0, label: 'Subgroups' }
// Labels.targetAgent[parentGroup] = { template: '{} encompass {}', index: 0 }
// Labels.targetAgent[steward] = { template: '{} steward {}', index: 0, label: 'Stewards' }
// Labels.targetAgent[stewardee] = { template: '{} are stewarded by {}', index: 0, label: 'Stewardees' }
// Labels.targetAgent[mentor] = { template: '{} mentor {}', index: 0, label: 'Mentors' }
// Labels.targetAgent[mentee] = { template: '{} are mentored by {}', index: 0, label: 'Mentees' }

Labels.targetAgent[member] = { template: 'People in {} ', index: 0, label: 'People' }
Labels.targetAgent[group] = { template: 'Groups', index: 0, label: 'Groups' }
Labels.targetAgent[subgroup] = { template: 'Subgroups of {}', index: 0, label: 'Subgroups' }
Labels.targetAgent[parentGroup] = { template: 'Parent-groups of {}', index: 0 }
Labels.targetAgent[steward] = { template: 'Stewards of {}', index: 0, label: 'Stewards' }
Labels.targetAgent[stewardee] = { template: 'Stewardees of {}', index: 0, label: 'Stewardees' }
Labels.targetAgent[mentor] = { template: 'Mentors of {}', index: 0, label: 'Mentors' }
Labels.targetAgent[mentee] = { template: 'Mentees of {}', index: 0, label: 'Mentees' }



Labels.Person = "People"
Labels.Group = "Groups"

// Inset:
Labels.targetAgent[memberLink] = { template: 'belongs to {}' }
Labels.targetAgent[parentGroupLink] = { template: 'encompasses {}' }
Labels.targetAgent[subgroupLink] = { template: 'are part of {}' }
Labels.targetAgent[groupLink] = { template: 'includes {}' }
Labels.targetAgent[mentorLink] = { template: 'mentors {}' }
Labels.targetAgent[menteeLink] = { template: 'are mentored by {}' }


Labels.targetAgent[stewardLink] = { template: 'stewards {}' }
Labels.targetAgent[stewardeeLink] = { template: 'are stewarded by {}' }

Labels.contextAgent[steward] = { template: 'a steward in {}' }
Labels.contextAgent[stewardee] = { template: 'a stewardee in {}' }

Labels.contextAgent[stewardeeLink] = { label: 'is stewarded by' }
Labels.contextAgent[stewardLink] = { label: 'is steward of' }

Labels.contextAgent[member] = { template: 'a steward in {}' }

Labels[member] = vars.nodesAndLinks[0]
Labels[group] = vars.nodesAndLinks[1]
Labels[steward] = vars.nodesAndLinks[2]
Labels[stewardee] = vars.nodesAndLinks[3]
Labels[mentor] = vars.nodesAndLinks[4]
Labels[mentee] = vars.nodesAndLinks[0]
Labels[subgroup] = vars.nodesAndLinks[0]
Labels[parentGroup] = vars.nodesAndLinks[0]




module.exports = Labels
