var find = require('lodash.find')
var forEach = require('lodash.foreach')
var debug = require('debug')('graph-view:make-links')

function makeLink (subj, relationship, obj) {
  return {
    source: subj.index,
    target: obj.index,
    model: relationship
  }
}

module.exports = function (predicates, edgeObj, nodes) {
  var links = []
  debug('edgeObj', edgeObj, predicates, nodes )

  forEach(predicates, function (predicate) {
    var subjectIds  = edgeObj[predicate]
      
    if (subjectIds) {
      Object.keys(subjectIds).forEach(function (subjId) {
        var subject   = find(nodes, function (node) {  return node["@id"] === subjId })
        var objectIds = edgeObj[predicate][subjId]
        
        forEach(objectIds, function (objId) {
          var object = find(nodes, function (node) { return node["@id"] === objId })
          
          if (object) {
            var relationship = find(subject.relationships.models, function (rel) {
              debug('rel', rel)
              return (
                rel.is['@id'] === subjId && 
                rel.has['@id'] === objId &&
                rel.relationshipType['@id'] === predicate
              )

            })
            
            if (relationship) links.push(makeLink(subject, relationship, object))
          }
        })
      })       
    }
  })

  return links
}
