var React = require('react')
var r = require('r-dom')
var Button = require('react-bootstrap/Button')

module.exports = React.createClass({
  render: function () {

    var relType = this.props.model
    var active = this.props.active
    var color = this.props.color

    var style = {
      backgroundColor: active ? color : '#e9e9e9',
      borderColor: active ? '#e9e9e9' : color,
      color: active ? '#f5f5f5' : '#333333'
    }

    return r(Button, { 
      onClick: this.props.toggleRelationship,
      style: style,
      active: active
    }, relType ? relType.pluralName : '')
  }
})
