var React 	= require('react')
var Button 	= require('react-bootstrap').Button
var config 	= require('uiconfig')

module.exports = React.createClass({
	render: function () {
		var d 			= this.props.datum
		var active 	= this.props.active
		var style 	= {
			backgroundColor: active ? this.props.color : '#e9e9e9',
			borderColor: active ? '#e9e9e9' : this.props.color,
			color: active ? '#f5f5f5' : '#333333'
		}

		return React.createElement(
			Button, 
			{ 
				onClick: this.props.toggleRelationship,
				"data-relationshipid": d.relationshipId,
				style: style,
				active: active
			}, 
			d.label
		) 
	}
})