import createComponent from 'util/create-component'
import test from 'tape'
import LegendItem from './'

const data = {
  color: 'red',
  showLink: true,
  label: 'follows',
  hasToggle: true,
  queryId: 1,
  onToggle: function (payload) {
    console.log(payload)
  }
}

test('the legend widget renders', t => {
  let component = createComponent(LegendItem, data)

  t.equal(component.type, 'div')
  t.equal(typeof LegendItem.prototype.handleToggle(data.queryId), 'function')
  t.end()
})
