var React = require('react')
var r = require('r-dom')
var mui = require('material-ui')
var Paper = mui.Paper
var debug = require('debug')('legend-widget')
var vars = require('ui/vars')

module.exports = React.createClass({

  propTypes: {
    legendItems: React.PropTypes.array
  },

  render: function () {
    debug('render legend-widget', this.props)

    return (
      r(Paper, {
        zDepth: 1,
        className: 'legend-widget',
        style: {
          position: 'absolute',
          left: '50%',
          transform: 'translateX(-50%)',
          display: 'flex',
          flexDirection: 'column',
          bottom: vars.materialKeyLines.statusBar
        }
      },
      this.props.legendItems)
    )
  }
})
