import { createSelector } from 'reselect'
import filter from 'lodash.filter'
const initialState = require('./reducers').initialState

// TEMP
const state = { queryLabels: initialState }

const queryLabelsById = state => state.queryLabels.byId

const relQueryLabels = (relId, agentType) => {
  return createSelector(
    [queryLabelsById],
    queryLabelsById => {
      if (agentType) {
        return filter(queryLabelsById, (label) => {
          return label.relationship === relId && label.agentType === agentType
        })
      } else {
        return filter(queryLabelsById, (label) => {
          return label.relationship === relId
        })
      }
    }
  )
}
