import test from 'tape'
import state from 'state'
import { queryTypesByRelationshipTypeId, queryTypesByAgentIdAndDirection } from './getters'

const expected = {
  'simon-is-member-of-holodex': {
    id: 'simon-is-member-of-holodex',
    type: 'is-member-of',
    source: 'simon',
    target: 'holodex'
  },
  'mikey-stewards-simon': {
    id: 'mikey-stewards-simon',
    type: 'stewards',
    source: 'mikey',
    target: 'simon',
    context: 'enspiral'
  }
}

test('it gets an object of query types indexed by relationship-id', t => {
  let actual = queryTypesByRelationshipTypeId(state)['is-member-of']

  t.deepEqual(actual, expected)
  t.end()
})


test('it gets an object of query types indexed by agent id and direction', t => {
  let actual = queryTypesByAgentIdAndDirection(state)

  t.deepEqual(actual, expected)
  t.end()
})
