import { GET_AGENT } from 'actions'


const initialState = [
  {
    id: 'mikey',
    name: 'Mikey',
    image: 'http://images.clipartpanda.com/t-rex-dinosaur-clip-art-T-Rex-Dinosaur_1.png'
  },
  {
    id: 'simon',
    name: 'Simon',
    image: 'http://www.cats.org.uk/uploads/images/pages/photo_latest14.jpg'
  }
]

function agents (state = initialState, action) {
  switch (action.type) {
  case GET_AGENT:
    return state.filter(agent => action.payload.id === agent.id)
  }


}
