import { GET_AGENT } from 'actions'


const initialState = {
  ids: ['mikey', 'simon', 'holodex', 'enspiral'],
  byId: {
    'mikey': {
      type: 'Person',
      name: 'Mikey',
      image: 'http://images.clipartpanda.com/t-rex-dinosaur-clip-art-T-Rex-Dinosaur_1.png'
    },
    'simon': {
      type: 'Person',
      name: 'Simon',
      image: 'http://www.cats.org.uk/uploads/images/pages/photo_latest14.jpg'
    },
    'holodex': {
      type: 'Group',
      name: 'Holodex',
      image: 'https://blog.dinosaur.is/content/images/2014/Oct/nation-holon1.png'
    },
    'enspiral': {
      type: 'Group',
      name: 'Enspiral',
      image: 'https://logo.clearbit.com/enspiral.com'
    }
  }
}


function agents (state = initialState, action) {
  switch (action.type) {
  case GET_AGENT:
    return state.byId[action.payload.id]
  }
}
