const mapKeys = require('object-keys-map')
const zipObject = require('lodash.zipobject')
const map = require('lodash.map')
const extend = require('xtend')


import { combineReducers } from 'redux'
import { relationshipGraph } from 'graph/reducers'
import types from 'types'
console.log('types', types)

// TODO tweak reducers to conform http://rackt.github.io/redux/docs/basics/Reducers.html
const typeReducers = zipObject(
  map(types, (type, key) => { return key }),
  map(types, type => { return type.reducers })
)
const reducers = extend(typeReducers, { relationshipGraph: relationshipGraph })
const holodex = combineReducers(reducers)

export default holodex
