const mapKeys = require('object-keys-map')
const zipObject = require('lodash.zipobject')
const map = require('lodash.map')
const extend = require('xtend')
const mapValues = require('lodash.mapvalues')
const debug = require('debug')('reducers')

import { relationshipGraph } from 'graph/reducers'
import types from 'types'


const holodex = (state, action) => {

  let typeReducers = mapValues(types, type => {
    return type.reducers(state, action)
  })

  return extend(typeReducers, {
    relationshipGraph: relationshipGraph(state, action)
  })
}


export default holodex
