import { GET_RELATIONSHIP } from 'actions'


const initialState = {
  ids: [
    'mikey-is-member-of-holodex',
    'simon-is-member-of-holodex',
    'holodex-is-subgroup-of-enspiral'
  ],
  byId: {
    'mikey-is-member-of-holodex': {
      type: 'is-member-of',
      source: 'mikey',
      target: 'holodex',
    },
    'simon-is-member-of-holodex': {
      type: 'is-member-of',
      source: 'simon',
      target: 'holodex',
    },
    'holodex-is-subgroup-of-enspiral': {
      type: 'is-subgroup-of',
      source: 'holodex',
      target: 'enspiral',
    }
  }
}


function relationships (state = initialState, action) {
  switch (action.type) {
  case GET_RELATIONSHIP:
    return state.byId[action.payload.id]
  }
}
