const roleTypesByRelationshipType = [
  ['roleTypes'],
  ['relationshipTypes'],
  function (relTypes, rolTypes) {
    return relTypes.map(function (relType, relTypeId) {
      return rolTypes.filter(function (rolType) {
        return rolType.relationshipType == relTypeId
      })
    })
  }
]

module.exports = {
  roleTypesByRelationshipType
}
