import test from 'tape'
import state from 'state'
import { relationshipTypesByAgentId } from './getters'

const expected = { 'is-member-of': { color: '#3F51B5', id: 'is-member-of', labels: [ '{source} belongs to {target}', '{source} is part of {target}' ], name: 'is member of' }, stewards: { color: '#F44336', id: 'stewards', labels: [ '{source} stewards {target}' ], name: 'stewards' } }

console.log(relationshipTypesByAgentId(state))
test('it gets an object of relationship types indexed by agent-id', t => {
  let actual = relationshipTypesByAgentId(state)['simon']

  t.deepEqual(actual, expected)
  t.end()
})
