'use strict';

const mapKeys = require('object-keys-map')
const debug = require('debug')('holodex:types')

const types = {
  AgentType: require('agent-type'),
  Agent: require('agent'),
  RelationshipType: require('relationship-type'),
  Relationship: require('relationship'),
  QueryText: require('query-text'),
  Query: require('query'),

}

types.indexedByCollection = mapKeys(types, function (typeName) {
  return types[typeName].vocab.collection
})

debug(types)

module.exports = types
