var mapKeys = require('object-keys-map')
var debug = require('debug')("holodex:types");

// describe types
var types = {
  Thing: {
    Model: require('base/model')
  },
  Agent: require('agent'),
  Person: require('person'),
  Group: require('group'),
  RoleType: require('role-type'),
  Role: require('role'),
  RelationshipType: require('relationship-type'),
  Relationship: require('relationship'),
};

types.indexedByCollection = mapKeys(types, function (key) {
  var type = types[key]
  debug(key, type.Model.prototype.collectionName)
  return type.Model.prototype.collectionName
})

debug(types);


module.exports = types;
