import { connect } from 'react-redux'
import component from 'virtual-component'
const debug = require('debug')('ui:index')
const Graph = require('graph')
const extend = require('xtend')


console.log('Graph', Graph, component)

function render (props) {
  debug(props)
  return Graph({ ...props, route: 'simon' })
}

const Ui = {
  render
}

module.exports = (props, children) => component(Ui, props, children)



