import { connect } from 'react-redux'
var React = require('react')
var r = require('r-dom')
var debug = require('debug')('ui:index')
var ThemeManager = require('material-ui/lib/styles/theme-manager')
var themeManager = new ThemeManager
var LightTheme = require('./light-theme')
themeManager.setTheme(LightTheme)

//getters
import { agentsById } from 'agent/getters'

const Ui = React.createClass({

  childContextTypes: {
    muiTheme: React.PropTypes.object
  },

  getChildContext: function () {
    return {
      muiTheme: themeManager.getCurrentTheme()
    }
  },

  render: function () {
    debug('props, state', this.props, this.state)


    return r.div({ className: 'test'})

  },

})

export default connect(agentsById)(Ui)
