//TODO use matrix transform independent of svg

module.exports  = function (DOMNode, elem) {
    var corners = {}
    var pt = DOMNode.createSVGPoint()
    var matrix  = elem.getScreenCTM()
    var bbox = elem.getBBox()
    pt.x = bbox.x
    pt.y = bbox.y
    corners.nw = pt.matrixTransform(matrix)
    pt.x += bbox.width
    corners.ne = pt.matrixTransform(matrix)
    pt.y += bbox.height
    corners.se = pt.matrixTransform(matrix)
    pt.x -= bbox.width
    corners.sw = pt.matrixTransform(matrix)
    pt.x += bbox.width / 2
    pt.y -= bbox.height / 2
    corners.center = pt.matrixTransform(matrix)
    return corners
}
